import React, { useState } from 'react';
import FileUpload from './components/FileUpload';
import ImageProcessor from './components/ImageProcessor';
import Invoice from './components/Invoice';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [extractedData, setExtractedData] = useState(null);

  const handleFileUpload = (uploadedFile) => {
    setFile(uploadedFile);
    setExtractedData(null); // Reset extracted data when a new file is uploaded
  };

  const handleDataExtraction = (data) => {
    setExtractedData(data);
  };

  return (
    <div className="App">
      <h1>AI Scanner</h1>
      <FileUpload onFileUpload={handleFileUpload} />
      {file && !extractedData && (
        <ImageProcessor file={file} onDataExtracted={handleDataExtraction} />
      )}
      {extractedData && <Invoice data={extractedData} />}
    </div>
  );
}

export default App;
